import React from "react";

export default function EnlargeImage() {
  function makeImagesEnlargable() {
    var images = document.querySelectorAll("img[data-enlargable]");
    images.forEach(function (img) {
      img.classList.add("img-enlargable");
      img.addEventListener("click", function () {
        var src = this.getAttribute("src");
        var overlay = document.createElement("div");
        overlay.style.cssText =
          "background: RGBA(0,0,0,.5) url(" +
          src +
          ") no-repeat center; background-size: contain; width: 100%; height: 100%; position: fixed; z-index: 10000; top: 0; left: 0; cursor: zoom-out;";
        overlay.addEventListener("click", function () {
          this.remove();
        });
        document.body.appendChild(overlay);
      });
    });
  }

  // Call the function to make images enlargable
}

EnlargeImage();
